<?php if(!defined('BASEPATH')) exit('No direct script access allowed!');class Model_fadmin extends CI_Model{	function __construct() {		parent::__construct();	}		public function get($table, $order_by='', $sort='ASC') {		$this->load->database();		$this->db->order_by($order_by, $sort);		$result = $this->db->get($table);		$this->db->close();		return $result;	}	public function select($condition, $table, $order_by='', $sort='ASC'){		$this->load->database();		$this->db->where($condition);		$this->db->order_by($order_by, $sort);		$result = $this->db->get($table);		$this->db->close();		return $result;	}	public function insert($data, $table) {		$this->load->database();		$result = $this->db->insert($table, $data);		$this->db->close();		return $result;	}	public function insert_id($data, $table) {		$this->load->database();		$result = $this->db->insert($table, $data);		$insert_id = $this->db->insert_id();		$this->db->close();		return $insert_id;	}	public function insert_batch($data, $table) {		$this->load->database();		$result = $this->db->insert_batch($table, $data);		$this->db->close();		return $result;	}	public function update($condition, $data, $table){		$this->load->database();		$this->db->where($condition);		$result = $this->db->update($table, $data);		$this->db->close();		return $result;	}	public function update_batch($table, $data, $field_title){		$this->load->database();		$result = $this->db->update_batch($table, $data, $field_title);		$this->db->close();		return $result;	}	public function delete($condition, $table) {		$this->load->database();		$this->db->where($condition);		$result = $this->db->delete($table);		$this->db->close();		return $result;	}	public function rawQuery($query) {		$this->load->database();		$result = $this->db->query($query);		$this->db->close();		return $result;	}	}